import{_ as s,a,c as n,e as l}from"./app.462b1e50.js";const A=JSON.parse('{"title":"GSAP","description":"","frontmatter":{"title":"GSAP"},"headers":[{"level":2,"title":"GreenSock","slug":"greensock"},{"level":2,"title":"\u2699\uFE0F \u5B89\u88C5 PNPM","slug":"\u2699\uFE0F-\u5B89\u88C5-pnpm"},{"level":2,"title":"\u2705 \u5F15\u5165","slug":"\u2705-\u5F15\u5165"},{"level":2,"title":"Getting Started","slug":"getting-started"},{"level":3,"title":"So what properties can I animate?","slug":"so-what-properties-can-i-animate"}],"relativePath":"pages/Tool/gsapIndex.md"}'),o={name:"pages/Tool/gsapIndex.md"},t=l(`<h1 id="\u5B98\u7F51\u5FEB\u901F\u5165\u95E8\u6559\u7A0B" tabindex="-1">\u5B98\u7F51\u5FEB\u901F\u5165\u95E8\u6559\u7A0B <a class="header-anchor" href="#\u5B98\u7F51\u5FEB\u901F\u5165\u95E8\u6559\u7A0B" aria-hidden="true">#</a></h1><h2 id="greensock" tabindex="-1">GreenSock <a class="header-anchor" href="#greensock" aria-hidden="true">#</a></h2><p>GSAP is a robust JavaScript toolset that turns developers into animation superheroes. Build high-performance animations that work in every major browser. Animate CSS, SVG, canvas, React, Vue, WebGL, colors, strings, motion paths, generic objects...anything JavaScript can touch! GSAP&#39;s ScrollTrigger plugin lets you create jaw-dropping scroll-based animations with minimal code. No other library delivers such advanced sequencing, reliability, and tight control while solving real-world problems on over 11 million sites. GSAP works around countless browser inconsistencies; your animations just work. At its core, GSAP is a high-speed property manipulator, updating values over time with extreme accuracy. It&#39;s up to 20x faster than jQuery! See <a href="https://greensock.com/why-gsap/" target="_blank" rel="noreferrer">https://greensock.com/why-gsap/</a> for what makes GSAP so special.</p><p>GSAP \u662F\u4E00\u4E2A\u5F3A\u5927\u7684 JavaScript \u5DE5\u5177\u96C6\uFF0C\u53EF\u4EE5\u5C06\u5F00\u53D1\u4EBA\u5458\u53D8\u6210\u52A8\u753B\u8D85\u7EA7\u82F1\u96C4\u3002\u6784\u5EFA\u9002\u7528\u4E8E\u6240\u6709\u4E3B\u6D41\u6D4F\u89C8\u5668\u7684\u9AD8\u6027\u80FD\u52A8\u753B\u3002\u52A8\u753B CSS\u3001SVG\u3001canvas\u3001React\u3001Vue\u3001WebGL\u3001\u989C\u8272\u3001\u5B57\u7B26\u4E32\u3001\u8FD0\u52A8\u8DEF\u5F84\u3001\u901A\u7528\u5BF9\u8C61\u2026\u2026JavaScript \u53EF\u4EE5\u89E6\u6478\u7684\u4EFB\u4F55\u4E1C\u897F\uFF01GSAP \u7684 ScrollTrigger \u63D2\u4EF6\u5141\u8BB8\u60A8\u7528\u6700\u5C11\u7684\u4EE3\u7801\u521B\u5EFA\u4EE4\u4EBA\u60CA\u53F9\u7684\u57FA\u4E8E\u6EDA\u52A8\u7684\u52A8\u753B\u3002\u6CA1\u6709\u5176\u4ED6\u5E93\u5728\u89E3\u51B3 1100 \u591A\u4E07\u4E2A\u7AD9\u70B9\u4E0A\u7684\u5B9E\u9645\u95EE\u9898\u7684\u540C\u65F6\u63D0\u4F9B\u5982\u6B64\u5148\u8FDB\u7684\u6392\u5E8F\u3001\u53EF\u9760\u6027\u548C\u4E25\u683C\u7684\u63A7\u5236\u3002GSAP \u89E3\u51B3\u4E86\u65E0\u6570\u6D4F\u89C8\u5668\u4E0D\u4E00\u81F4\u7684\u95EE\u9898\uFF1B\u4F60\u7684\u52A8\u753B\u521A\u521A\u597D\u7528\u3002\u5176\u6838\u5FC3\u662F\uFF0CGSAP \u662F\u4E00\u79CD\u9AD8\u901F\u5C5E\u6027\u64CD\u7EB5\u5668\uFF0C\u80FD\u591F\u968F\u65F6\u95F4\u4EE5\u6781\u9AD8\u7684\u7CBE\u5EA6\u66F4\u65B0\u503C\u3002\u5B83\u6BD4 jQuery \u5FEB 20 \u500D\uFF01\u67E5\u770B <a href="https://greensock.com/why-gsap/" target="_blank" rel="noreferrer">https://greensock.com/why-gsap/</a> \u662F\u4EC0\u4E48\u8BA9 GSAP \u5982\u6B64\u7279\u522B\u3002</p><h2 id="\u2699\uFE0F-\u5B89\u88C5-pnpm" tabindex="-1">\u2699\uFE0F \u5B89\u88C5 PNPM <a class="header-anchor" href="#\u2699\uFE0F-\u5B89\u88C5-pnpm" aria-hidden="true">#</a></h2><div class="language-sh"><button class="copy"></button><span class="lang">sh</span><pre><code><span class="line"><span style="color:#A6ACCD;">pnpm install gsap</span></span>
<span class="line"></span></code></pre></div><h2 id="\u2705-\u5F15\u5165" tabindex="-1">\u2705 \u5F15\u5165 <a class="header-anchor" href="#\u2705-\u5F15\u5165" aria-hidden="true">#</a></h2><div class="language-ts"><button class="copy"></button><span class="lang">ts</span><pre><code><span class="line"><span style="color:#676E95;">// typical import  \u666E\u901A\u5F15\u5165</span></span>
<span class="line"><span style="color:#89DDFF;">import</span><span style="color:#A6ACCD;"> gsap </span><span style="color:#89DDFF;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">gsap</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">// get other plugins:  \u5176\u4ED6\u63D2\u4EF6:</span></span>
<span class="line"><span style="color:#89DDFF;">import</span><span style="color:#A6ACCD;"> ScrollTrigger </span><span style="color:#89DDFF;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">gsap/ScrollTrigger</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#89DDFF;">import</span><span style="color:#A6ACCD;"> Flip </span><span style="color:#89DDFF;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">gsap/Flip</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#89DDFF;">import</span><span style="color:#A6ACCD;"> Draggable </span><span style="color:#89DDFF;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">gsap/Draggable</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">// or all tools are exported from the &quot;all&quot; file (excluding members-only plugins):</span></span>
<span class="line"><span style="color:#89DDFF;">import</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">gsap</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">ScrollTrigger</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">Draggable</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">MotionPathPlugin</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">gsap/all</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">// don&#39;t forget to register plugins</span></span>
<span class="line"><span style="color:#A6ACCD;">gsap</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">registerPlugin</span><span style="color:#A6ACCD;">(ScrollTrigger</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> Draggable</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> Flip</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> MotionPathPlugin)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span></code></pre></div><h2 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-hidden="true">#</a></h2><ul><li><code>gsap.to(&quot;.circle1&quot;, { x: 40, fill: &#39;blue&#39; })</code></li><li><code>gsap.from(&quot;.circle&quot;, { x: -40, fill: &#39;blue&#39;, })</code></li><li><code>gsap.fromTo( &quot;.circle&quot;,{ x: -40, fill: &#39;blue&#39;, }, { x: 40, fill: &#39;green&#39; })</code></li><li><code>gsap.set(&quot;.circle&quot;, { x: 40, fill: &#39;blue&#39;, })</code></li></ul><div class="tip custom-block"><p class="custom-block-title">\u9009\u62E9\u6807\u7B7E</p><p>\u53EF\u4EE5\u4F7F\u7528 CSS \u9009\u62E9\u5668\u6765\u9009\u62E9\u9700\u8981\u6DFB\u52A0\u52A8\u753B HTML \u6807\u7B7E\uFF0C\u4E5F\u53EF\u4EE5\u83B7\u53D6 DOM \u7684\u65B9\u5F0F\uFF0C\u5982:</p><div class="language-ts"><button class="copy"></button><span class="lang">ts</span><pre><code><span class="line"><span style="color:#C792EA;">let</span><span style="color:#A6ACCD;"> box </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> document</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">querySelector</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">.box</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">gsap</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">to</span><span style="color:#A6ACCD;">(box</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#A6ACCD;"> </span><span style="color:#F07178;">x</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">200</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">// or even an Array of element \u751A\u81F3\u662F\u4E00\u4E2A\u6807\u7B7E\u6570\u7EC4</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> a </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> document</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">querySelector</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">.a</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> b </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> document</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">querySelector</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">.b</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">gsap</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">to</span><span style="color:#A6ACCD;">([a</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> b]</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#A6ACCD;"> </span><span style="color:#F07178;">x</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">200</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span></code></pre></div></div><h3 id="so-what-properties-can-i-animate" tabindex="-1">So what properties can I animate? <a class="header-anchor" href="#so-what-properties-can-i-animate" aria-hidden="true">#</a></h3><p>\u90A3\u4E48\uFF0C\u6211\u53EF\u4EE5\u8BBE\u7F6E\u90A3\u4E9B\u52A8\u753B\u5462\uFF1F\u5728 <code>options</code> \u53EF\u9009\u9879\u91CC:</p><table><thead><tr><th>GSAP</th><th style="text-align:center;">CSS</th></tr></thead><tbody><tr><td>x: 100</td><td style="text-align:center;">transform: translateX(100px)</td></tr><tr><td>y: 100</td><td style="text-align:center;">transform: translateY(100px)</td></tr><tr><td>xPercent: -50</td><td style="text-align:center;">transform: translateX(-50%)</td></tr><tr><td>ypercent: -50</td><td style="text-align:center;">transform: translateY(-50%)</td></tr><tr><td>rotation: 360</td><td style="text-align:center;">transform: rotate(360deg)</td></tr><tr><td>rotationX: 360</td><td style="text-align:center;">transform: rotateX(360deg)</td></tr><tr><td>rotationY: 360</td><td style="text-align:center;">transform: rotateY(360deg)</td></tr><tr><td>scale: 2</td><td style="text-align:center;">transform: scale(2, 2)</td></tr><tr><td>scaleX: 2</td><td style="text-align:center;">transform: scaleX(2, 2)</td></tr><tr><td>scaleY: 2</td><td style="text-align:center;">transform: scaleY(2, 2)</td></tr><tr><td>transformOrigin: &quot;0% 100%&quot;</td><td style="text-align:center;">transform-origin: 0% 100%</td></tr><tr><td>skewX: 45</td><td style="text-align:center;">transform: skewX(45deg)</td></tr><tr><td>skewY: 45</td><td style="text-align:center;">transform: skewY(45deg)</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">\u9009\u9879\u5176\u4ED6\u5199\u6CD5</p><p>By default GSAP will use px and degrees for transforms but you can use other units like, vw, radians or even do your own JS calculations or relative values!</p><p>\u9ED8\u8BA4\u60C5\u51B5\u4E0B\uFF0CGSAP \u5C06\u4F7F\u7528 px \u548C\u5EA6\u6570\u8FDB\u884C\u53D8\u6362\uFF0C\u4F46\u60A8\u53EF\u4EE5\u4F7F\u7528\u5176\u4ED6\u5355\u4F4D\uFF0C\u4F8B\u5982 vw\u3001\u5F27\u5EA6\uFF0C\u751A\u81F3\u53EF\u4EE5\u8FDB\u884C\u81EA\u5DF1\u7684 JS \u8BA1\u7B97\u6216\u76F8\u5BF9\u503C\uFF01</p><div class="language-ts"><button class="copy"></button><span class="lang">ts</span><pre><code><span class="line"><span style="color:#FFCB6B;">x</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">200</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;">// use default of px</span></span>
<span class="line"><span style="color:#FFCB6B;">x</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">+=200</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;">// relative values</span></span>
<span class="line"><span style="color:#FFCB6B;">x</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">40vw</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;">// or pass in a string with a different unit for GSAP to parse</span></span>
<span class="line"><span style="color:#FFCB6B;">x</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">()</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">=&gt;</span><span style="color:#A6ACCD;"> window</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">innerWidth </span><span style="color:#89DDFF;">/</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">2</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;">// you can even use functional values to do a calculation!</span></span>
<span class="line"></span>
<span class="line"><span style="color:#FFCB6B;">rotation</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">360</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;">// use default of degrees</span></span>
<span class="line"><span style="color:#FFCB6B;">rotation</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">1.25rad</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;">// use radians</span></span>
<span class="line"></span></code></pre></div></div>`,15),e=[t];function p(r,c,i,D,y,F){return a(),n("div",null,e)}const C=s(o,[["render",p]]);export{A as __pageData,C as default};
